<script lang="ts">
    interface Togglers {
        config: boolean;
        filter: boolean;
        about: boolean;
        search: boolean;
        [key: string]: boolean;
    }
	export let open: Togglers = {
        config: true,
        filter: false,
        about: false,
        search: false,
    }

    function toggleMenus(active: string = "") {
        Object.keys(open).forEach(key => {
            open[key] = false;
            open[active] = true;
        })
    }

</script>
